import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFacebook, faInstagram, faGoogle, faGithub} from '@fortawesome/free-brands-svg-icons';
import { faEnvelope, faLock, faPhone } from '@fortawesome/free-solid-svg-icons'
import logo from '../images/auth-logo.svg'
import ValidateButton from '../components/validateButton'
import '../css/pages/signup.scss'
import { Link } from 'react-router-dom'
import axios from 'axios'

function SignUp() {

  const [emailData, setEmailData] = useState('')
  const [passwordData, setPasswordData] = useState('')
  const [phonedData, setPhoneData] = useState('')

  const submitSignup = async (e) => {
    e.preventDefault();  // Empêche le lien de recharger la page

    const userData = {
      email: emailData,
      password: passwordData,
      phone: phoneData,
    };

    try {
      const res = await axios.post('http://localhost:5000/signup', userData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      console.log('created', res.data);
    } catch (err) {
      console.error('error creating user', err);
    }
  };

  return (

    <div className='signup-container'>

      <div className='logo'>
        <img src={logo} alt='logo-dev' />
        <span>devchallenges</span>
      </div>

      <div className="presentation-text">
        <p>Join thousands of learners from around the world</p>
      </div>

      <div className="title-page">
        <p>Create an account</p>
      </div>

      <div className="inputs">

        <div className='input'>
          <FontAwesomeIcon icon={faEnvelope} />
          <input type="email" placeholder='Email' value={emailData} onChange={(e) => setEmailData(e.target.value)}/>
        </div>

        <div className='input'>
          <FontAwesomeIcon icon={faLock} />
          <input type="password" placeholder='Password' value={passwordData} onChange={(e) => setPasswordData(e.target.value)}/>
        </div>

        <div className='input'>
        <FontAwesomeIcon icon={faPhone} />
          <input type="text" placeholder='Phone number' value={phonedData} onChange={(e) => setPhoneData(e.target.value)}/>
        </div>

      </div>

      <Link className="validation" to= '/signup' onClick={submitSignup}>
        <ValidateButton />
      </Link>

      <div className="social-container">
        <div className="social-link">
          <p>or continue with these social profile</p>
          <div className="social-logos">
            <FontAwesomeIcon icon={faFacebook} />
            <FontAwesomeIcon icon={faInstagram} />
            <FontAwesomeIcon icon={faGoogle} />
            <FontAwesomeIcon icon={faGithub} />
          </div>
        </div>
      </div>
    </div>
  )
}

export default SignUp
