import '../../css/components/chat_group/messageCard.scss'

function MessageCard({text, userList, author}) {


  useEffect(() => {

    const messageAuthor = () =>{
      return userList.find(user => user._id === author)
    }

    if(messageAuthor) {
      console.log(messageAuthor())
    }

  }, [userList, author])



  return (
    <div className='message-card-container'>
      {text}

      <img src={':'} alt='avatar' />
    </div>
  )
}

export default MessageCard
