import React, { useEffect, useState } from 'react';
import '../../css/components/chat_group/messageCard.scss';

function MessageCard({ text, userList, author }) {
  const [authorInfo, setAuthorInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setIsLoading(true); // Marquer le chargement comme en cours

    const fetchAuthorInfo = async () => {
      try {
        const foundAuthor = userList.find((user) => user._id === author);
        setAuthorInfo(foundAuthor);
        setIsLoading(false); // Marquer le chargement comme terminé
      } catch (error) {
        console.error('Error fetching author info:', error);
        setIsLoading(false); // En cas d'erreur, marquer le chargement comme terminé pour éviter un blocage
      }
    };

    fetchAuthorInfo();
  }, [userList, author]);

  return (
    <div className='message-card-container'>
      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <>
          {authorInfo && (
            <div className='message-infos'>
              <div className='author-avatar'>
                <img
                  src={`http://localhost:5000${authorInfo.photo}`}
                  alt='avatar'
                />
              </div>

              <div className='infos'>{authorInfo.pseudo}</div>
            </div>
          )}

          <div className='text'>{text}</div>
        </>
      )}
    </div>
  );
}

export default MessageCard;
