import React, { useState, useEffect } from 'react'
import '../css/pages/userEdit.scss'
import { useAuth } from '../functions/auth/authContext'
import { faEnvelope, faLock, faPhone } from '@fortawesome/free-solid-svg-icons'
import ValidateButton from '../components/validateButton'
import axios from 'axios'
import TopBar from '../components/topBar'
import avatar from '../images/avatar.jpg'
import InputForm from '../components/auth/inputForm'

function UserEdit() {

  const { token, userEmail } = useAuth()
  const [userInfos, setUserInfos] = useState(null)
  const [emailData, setEmailData] = useState('')
  const [phoneData, setPhoneData] = useState('')
  const [passwordData, setPasswordData] = useState('')

  useEffect(() => {
    if (token) {
      axios.get('http://localhost:5000/userinfos', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })

      .then(res => {
        res.data.forEach(user => {
          if(user.email === userEmail)
            setUserInfos(user)
        });

      })
      .catch(err => {
        console.error('Error fetching user info', err)
      })
    }
  }, [token])

  const handleSubmit = (e) => {
    e.preventDefault()


    console.log('coucou')

    /* axios.put(`http://localhost:5000/userinfos/edit/${id}`) */
  }

  return (
    <div className='userEdit-container'>
      <TopBar />

      <div className='page-title'>
        <h1>Change info</h1>
        <p>changes will be reflected to every services</p>
      </div>

      <div className="infos-list">

        <div className="info-image">
          <div className="profile-image">
            <img src={avatar} alt='profile-picture' width='50px' />
          </div>
          <p>Change photo</p>
        </div>

        <div className="info">
          <p>Email</p>
          <InputForm
            type='text'
            placeholder='Email@adress.com'
            value={emailData}
            onChange={(e) => setEmailData(e.target.value)}
            icon={faEnvelope}
          />
        </div>

        <div className="info">
          <p>Phone</p>
          <InputForm
            type='text'
            placeholder='0658741239'
            value={phoneData}
            onChange={(e) => setPhoneData(e.target.value)}
            icon={faPhone}
          />
        </div>

        <div className="info">
          <p>Password</p>
          <InputForm
            type='password'
            placeholder='00000000'
            value={passwordData}
            onChange={(e) => setPasswordData(e.target.value)}
            icon={faLock}
          />
        </div>

        <div className="validation">

          <ValidateButton
            text= 'Submit'
            onclick={handleSubmit}
          />
        </div>
      </div>

    </div>
  )
}

export default UserEdit
