import React from 'react'

function MessageCard({author, message}) {

  const messageDate = (message) => {
    if (!message || !message.date) {
      console.error("Message ou date non définis");
      return;
    }

    const today = new Date();
    const date = today.toLocaleDateString('en-US').replace(/(\d+)\/(\d+)\/(\d+)/, '$3/$1/$2');

    const messageFormattedDate = message.date.substring(0, 10)
      .split('-')
      .map(part => String(part).replace(/^0/, '')) // Retirer le zéro initial
      .join('/');

    console.log(messageFormattedDate, date);
  }


  messageDate()

  return (
    <div key={message._id} className='message-card-container'>
      <div className='message-infos'>
        <div className='author-avatar'>
          <img src={`http://localhost:5000${author.photo}`} alt='avatar' />
        </div>
        <div className='infos'>
          {author.pseudo}

        </div>
      </div>
      <div className='text'>{message.text}</div>
    </div>
  )
}

export default MessageCard
