import React, { useEffect, useState } from 'react';
import '../../css/components/chat_group/messageCard.scss';

function MessageCard({ text, userList, author }) {
  const [authorInfo, setAuthorInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true); // Nouvel état pour gérer le chargement

  useEffect(() => {
    const foundAuthor = userList.find((user) => user._id === author);
    setAuthorInfo(foundAuthor);
  }, [userList, author]);

  useEffect(() => {
    if (authorInfo) {
      setIsLoading(false); // Marque le chargement comme terminé une fois que authorInfo est défini
    }
  }, [authorInfo]);

  return (
    <div className='message-card-container'>
      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <div className='message-infos'>
          <div className='author-avatar'>
            <img
              src={authorInfo ? `http://localhost:5000${authorInfo.photo}` : 'loading'}
              alt='avatar'
            />
          </div>

          <div className='infos'>{authorInfo ? authorInfo.pseudo : 'loading'}</div>
        </div>
      )}

      <div className='text'>{text}</div>
    </div>
  );
}

export default MessageCard;
