import React, { useState, useEffect } from 'react';
import '../css/pages/userEdit.scss';
import { useAuth } from '../functions/auth/authContext';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faLock, faPhone, faArrowLeft, faCamera } from '@fortawesome/free-solid-svg-icons';
import ValidateButton from '../components/validateButton';
import axios from 'axios';
import TopBar from '../components/topBar';
import InputForm from '../components/auth/inputForm';
import { Link, useNavigate } from 'react-router-dom';

function UserEdit() {
  const navigate = useNavigate();
  const { token, userEmail } = useAuth();

  const [userInfos, setUserInfos] = useState(null);
  const [photoData, setPhotoData] = useState('');
  const [emailData, setEmailData] = useState('');
  const [phoneData, setPhoneData] = useState('');
  const [passwordData, setPasswordData] = useState('');

  useEffect(() => {
    if (token) {
      axios.get('http://localhost:5000/userinfos', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then(res => {
          res.data.forEach(user => {
            if (user.email === userEmail) {
              setUserInfos(user);
            }
          });
        })
        .catch(err => {
          console.error('Error fetching user info', err);
        });
    }
  }, [token]);

  useEffect(() => {
    if (userInfos) {
      setEmailData(userInfos.email || '');
      setPhoneData(userInfos.phone || '');
      setPhotoData(userInfos.photo || '');
    }
  }, [userInfos]);

  const handleFileChange = (e) => {
    setPhotoData(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const id = userInfos._id;

    const formData = new FormData();
    formData.append('email', emailData);
    formData.append('phone', phoneData);
    formData.append('password', passwordData);
    if (photoData) {
      formData.append('photo', photoData);
    }

    axios.put(`http://localhost:5000/userinfos/edit/${id}`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${token}`,
      },
    })
      .then(res => {
        console.log('User updated!');
        axios.get(`http://localhost:5000/userinfos/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
          .then(response => {
            setUserInfos(response.data);
            navigate('/userinfos');
          })
          .catch(err => {
            console.error('Error fetching updated user info', err);
          });
      })
      .catch(err => {
        console.error('Error updating user:', err);
      });
  };

  return (
    <div className='userEdit-container'>
      <TopBar />

      <div className="back-button">
        <FontAwesomeIcon icon={faArrowLeft} />
        <Link to='/userinfos'>Back</Link>
      </div>

      <div className='page-title'>
        <h1>Change info</h1>
        <p>Changes will be reflected to every service</p>
      </div>

      <form className="infos-list" onSubmit={handleSubmit}>

        <div className="info-image">
          <div className="profile-image">
            <FontAwesomeIcon className='icon-camera' icon={faCamera} />
            {userInfos && (
              <img
                src={`http://localhost:5000${userInfos.photo}?${new Date().getTime()}`}
                alt='profile-picture'
                width='50px'
              />
            )}
          </div>
          <input type="file" onChange={handleFileChange} accept="image/*" />
          <p>Change photo</p>
        </div>

        <div className="info">
          <p>Email</p>
          <InputForm
            type='text'
            placeholder='Email@address.com'
            value={emailData}
            onChange={(e) => setEmailData(e.target.value)}
            icon={faEnvelope}
          />
        </div>

        <div className="info">
          <p>Phone</p>
          <InputForm
            type='text'
            placeholder='0658741239'
            value={phoneData}
            onChange={(e) => setPhoneData(e.target.value)}
            icon={faPhone}
          />
        </div>

        <div className="info">
          <p>Password</p>
          <InputForm
            type='password'
            placeholder='00000000'
            value={passwordData}
            onChange={(e) => setPasswordData(e.target.value)}
            icon={faLock}
          />
        </div>

        <div className="validation">
          <ValidateButton text='Submit' />
        </div>
      </form>
    </div>
  );
}

export default UserEdit;
