import React, { useState, useEffect } from 'react';
import LayoutChat from '../components/chat_group/layoutChat';
import axios from 'axios';
import { useAuth } from '../functions/auth/authContext';

function Chat() {
  const [token] = useAuth();
  const [channels, setChannels] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/chat', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
    .then(res => {
      setChannels(res.data);
    })
    .catch(error => {
      console.error('Error fetching channels', error);
    });

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // Utilisation d'une dépendance vide pour s'exécuter une seule fois

  return (
    <div>
      {/* Vérifiez que channels est défini avant de passer à LayoutChat */}
      {channels && <LayoutChat channels={channels} />}
    </div>
  );
}

export default Chat;
