import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFacebook, faInstagram, faGoogle, faGithub } from '@fortawesome/free-brands-svg-icons';
import { faEnvelope, faLock, faPhone } from '@fortawesome/free-solid-svg-icons';
import logo from '../images/auth-logo.svg';
import '../css/pages/signup.scss';
import axios from 'axios';

function SignUp() {
  const [emailData, setEmailData] = useState('');
  const [passwordData, setPasswordData] = useState('');
  const [phoneData, setPhoneData] = useState('');

  const submitSignup = (e) => {
    e.preventDefault();

    const userData = {
      email: emailData,
      password: passwordData,
      phone: phoneData,
    };

    axios.post('http://localhost:5000/signup', userData, {
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(res => {
        console.log('User created:', res.data);
      })
      .catch(err => {
        console.error('Error creating user:', err);
      });
  };

  return (
    <div className='signup-container'>
      <div className='logo'>
        <img src={logo} alt='logo-dev' />
        <span>devchallenges</span>
      </div>

      <div className="presentation-text">
        <p>Join thousands of learners from around the world</p>
      </div>

      <div className="title-page">
        <p>Create an account</p>
      </div>

      <form className="inputs" onSubmit={submitSignup}>
        <div className='input'>
          <FontAwesomeIcon icon={faEnvelope} />
          <input
            type="email"
            placeholder='Email'
            value={emailData}
            onChange={(e) => setEmailData(e.target.value)}
          />
        </div>

        <div className='input'>
          <FontAwesomeIcon icon={faLock} />
          <input
            type="password"
            placeholder='Password'
            value={passwordData}
            onChange={(e) => setPasswordData(e.target.value)}
          />
        </div>

        <div className='input'>
          <FontAwesomeIcon icon={faPhone} />
          <input
            type="text"
            placeholder='Phone number'
            value={phoneData}
            onChange={(e) => setPhoneData(e.target.value)}
          />
        </div>

        <button type="submit" className="validation">
          Create
        </button>
      </form>

      <div className="social-container">
        <div className="social-link">
          <p>or continue with these social profile</p>
          <div className="social-logos">
            <FontAwesomeIcon icon={faFacebook} />
            <FontAwesomeIcon icon={faInstagram} />
            <FontAwesomeIcon icon={faGoogle} />
            <FontAwesomeIcon icon={faGithub} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
