import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faLock, faPhone } from '@fortawesome/free-solid-svg-icons'
import logo from '../images/auth-logo.svg'
import ValidateButton from '../components/validateButton'
import '../css/pages/signup.scss'
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios'
import SocialLinks from '../components/auth/socialLinks';

function SignUp() {

  const navigate = useNavigate()

  const [emailData, setEmailData] = useState('')
  const [passwordData, setPasswordData] = useState('')
  const [phoneData, setPhoneData] = useState('')

  const submitSignup = (e) => {
    e.preventDefault()

    const userData = {
      email: emailData,
      password: passwordData,
      phone: phoneData,
    };

    axios.post('http://localhost:5000/signup', userData, {
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(res => {
        console.log('User created !')
        navigate('/signin')
      })
      .catch(err => {
        console.error('Error creating user:', err)
      })
  }

  return (

    <div className='signup-container'>

      <div className='logo'>
        <img src={logo} alt='logo-dev' />
        <span>devchallenges</span>
      </div>

      <div className="presentation-text">
        <p>Join thousands of learners from around the world</p>
      </div>

      <div className="title-page">
        <p>Create new account</p>
      </div>

      <div className="inputs">

        <div className='input'>
          <FontAwesomeIcon icon={faEnvelope} />
          <input type="email" placeholder='Email' value={emailData} onChange={(e) => setEmailData(e.target.value)}/>
        </div>

        <div className='input'>
          <FontAwesomeIcon icon={faLock} />
          <input type="password" placeholder='Password' value={passwordData} onChange={(e) => setPasswordData(e.target.value)}/>
        </div>

        <div className='input'>
        <FontAwesomeIcon icon={faPhone} />
          <input type="text" placeholder='Phone number' value={phoneData} onChange={(e) => setPhoneData(e.target.value)}/>
        </div>
        <div className="signin-link">
          Already have an account? &nbsp;
          <Link to='/signin'>Sign in</Link>
        </div>

      </div>

      <Link className="validation" to= '/signup' onClick={submitSignup}>
        <ValidateButton />
      </Link>

      <SocialLinks />

    </div>
  )
}

export default SignUp
