import React from 'react'

function MessageCard({author, message}) {

  const messageDate = () => {

    const today = new Date();
    const date = today.toLocaleDateString('en-US').replace(/(\d+)\/(\d+)\/(\d+)/, '$3/$1/$2');
    const messageDate = message.date.substring(0,10).split('-').join('/').split('0').join('');

    return console.log(messageDate, date )
  }

  messageDate()

  return (
    <div key={message._id} className='message-card-container'>
      <div className='message-infos'>
        <div className='author-avatar'>
          <img src={`http://localhost:5000${author.photo}`} alt='avatar' />
        </div>
        <div className='infos'>
          {author.pseudo}

        </div>
      </div>
      <div className='text'>{message.text}</div>
    </div>
  )
}

export default MessageCard
